import React from "react";
import '../style/todoForm.sass'
import todo from "../store/todo";
import { observer } from "mobx-react-lite";

interface TodoProps{
		addTask(title: string):void
	}

const TodoForm:React.FC<TodoProps> = observer((props) => {



    return (
        <div className="form" onKeyDown={todo.keyDown}>
            <input className="form__input" value={todo.inputValue} onChange={todo.handleChange} />
            <div className="form__button"
                onClick={() => todo.addTask()}
                >add task</div>
        </div>
    )
})


export default TodoForm
