import { makeAutoObservable } from 'mobx'
import { v4 as uuidv4 } from 'uuid';

class Todo {
    todos = []
    inputValue = ''
    // textInput = useRef()

    constructor() {
        makeAutoObservable(this)
    }

    addTask () {
        console.log('fvfv');
        if (this.inputValue != '') {
            this.todos.push({
                title: this.inputValue,
                id: uuidv4(),
                status: true
            })
        }
        console.log(this.todos);
    }
    keyDown (ev) {
        if (ev.keyCode === 13) {
            name()
        }
    }


    handleChange (e) {
        this.inputValue = e
    };
}

export default new Todo();
