import React, { useState, useEffect } from "react";
import '../style/todoList.sass'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrash, faCheck } from '@fortawesome/free-solid-svg-icons'
import { observer } from "mobx-react-lite";
import todo from "../store/todo";


const AddList = observer(({ todos, setTodo }) => {

    const [currentStatus, setCurrentStatus] = useState(todos);

    useEffect(() => {
        localStorage.setItem('todo', JSON.stringify(todos))
    }, [todos])

    useEffect(() => {
        setCurrentStatus(todos)
    }, [todos])





    function TodoFilters (status) {
        if (status == 'true') {
            status = true
        }
        if (status == 'false') {
            status = false
        }
        if (status === 'all') {
            setCurrentStatus(todos)
        } else {
            const newTodo = [...todos].filter(item => (item.status === status))
            setCurrentStatus(newTodo)
        }
    }

    return (
        <div className="taskWrapper content">
            <div className="taskWrapper__todo">
                <select className="optionsFilters" onChange={(e) => {
                    const optin = e.target.value
                    TodoFilters(optin)
                }}>
                    <option value='all'>All task</option>
                    <option value='false' >Сompleted tasks</option>
                    <option value='true'>Unfinished tasks</option>
                </select>

                <div className="task">
                    {
                        todo.todos.map((item) => (

                            <div className={!item.status ? 'task__item task__close' : 'task__item task__open'} key={item.id}>
                                <div>
                                    <p className="task__name"  >{item.title}</p>
                                </div>
                                <div className="task__btns btn">
                                    <div className="btn__status" onClick={() => todo.statusTask(item.id)} type="">
                                        {
                                            item.status === false ? <FontAwesomeIcon className="btn__statusDone" icon={faCheck} /> : <FontAwesomeIcon icon={faCheck} />}
                                    </div>
                                    <div className="btn__delite" onClick={() => todo.deliteTask(item.id)} type=""><FontAwesomeIcon icon={faTrash} /></div>
                                </div>
                            </div>

                        ))
                    }

                </div>
            </div>

            <div className="filters">
                <div className="filters__btn" onClick={() => TodoFilters('all')}>All task</div>
                <div className="filters__btn" onClick={() => TodoFilters(false)}>Сompleted tasks</div>
                <div className="filters__btn" onClick={() => TodoFilters(true)}>Unfinished tasks</div>
            </div >
        </div>
    )
})


export default AddList
