import React, {useState} from "react";
import '../style/todoForm.sass'
import todo from "../store/todo";
import { observer } from "mobx-react-lite";

// interface TodoProps{
// 		addTask(title: string):void
// 	}

const TodoForm:React.FC = observer(() => {

        const [ddd, setddd] = useState('')
    function handleChange(event: React.ChangeEvent<HTMLInputElement>) {
		console.log(this.inputValue = 'dscdc');
		setddd(event.target.value)

		// this.inputValue = event.target.value;
	}

    return (
        <div className="form" onKeyDown={todo.keyDown}>
            <input className="form__input" value={ddd} onChange={handleChange} />
            <div className="form__button"
                onClick={() => todo.addTask()}
                >add task</div>
        </div>
    )
})


export default TodoForm
