import React, { useRef, useEffect } from "react";
import { v4 as uuidv4 } from 'uuid';
import '../../style/TodoForm/todoForm.sass'

function TodoForm ({ getInput, setinput }) {

    useEffect(() => {
        const onKeypress = e => console.log(e);

        // document.addEventListener('keypress', onKeypress);
        console.log(onKeypress.code == 'Enter');

    }, []);


    let textInput = useRef()
    function name () {
        if (textInput.current.value) {
            setinput([...getInput, {
                title: textInput.current.value,
                id: uuidv4(),
                status: true
            }])
        }
    }
    return (
        <div className="form" >
            <input className="form__input" ref={textInput} />
            <button className="form__button" onClick={name} type="">add task</button>
        </div>
    )
}


export default TodoForm
