import React from 'react';
import './style/style.sass'
import TodoForm from './components/TodoForm/TodoForm';
import AddList from './components/AddList/AddList';
import TodoFilters from './components/filters/filters';

import { useState } from 'react'


function App () {
  const getListTodo = () => {
    const todo = localStorage.getItem('todo')
    if (todo) {
      return JSON.parse(todo)
    } else {
      return []
    }
  }

  const [getInput, setinput] = useState(getListTodo());
  // const [currentStatus, setCurrentStatus] = useState(getInput);
  return (
    <div className="App" >
      <div className='header content'>
        <div className="content">
          <TodoForm getInput={getInput} setinput={setinput} />
          <div className='header__title'>
            todo List
          </div>
          <div />

        </div>
        <AddList getInput={getInput} setinput={setinput} />

      </div>
    </div>
  );
}

export default App;
