import React from 'react';
import './style/style.sass'
import TodoForm from './components/TodoForm/TodoForm';
import AddList from './components/AddList/AddList';
import TodoFilters from './components/filters/filters';

import { useState } from 'react'


function App () {
  const [getInput, setinput] = useState([]);
  const [currentStatus, setCurrentStatus] = useState(getInput);

  return (
    <div className="App" >
      <div className='header'>

        <TodoForm getInput={getInput} setinput={setinput} />
        <TodoFilters getInput={getInput} currentStatus={currentStatus} setCurrentStatus={setCurrentStatus} />
      </div>
      <AddList getInput={getInput} setinput={setinput} />


    </div>
  );
}

export default App;
