// import React, { useRef } from "react";
import { makeAutoObservable } from 'mobx'
import { decorate, observer } from 'mobx-react';
import { v4 as uuidv4 } from 'uuid';

class Todo {
    todos = []
    inputValue = ''
    // textInput = useRef()

    // constructor() {
    //     makeAutoObservable(this)
    // }

    Todo = decorate(Todo, {
        todos: observer
    });

    addTask () {
        if (this.inputValue != '') {
            this.todos.push({
                title: this.inputValue,
                id: uuidv4(),
                status: true
            })
        }
    }
    keyDown (ev) {
        if (ev.keyCode === 13) {
            this.addTask()
        }
    }

    handleChange (e) {
        this.inputValue = e
    };


    statusTask (id) {
        this.todos.filter(item => {
            if (item.id === id) {
                item.status = !item.status
            }
            return item
        })
    }

    deliteTask (id) {
        this.todos.filter(item => (
            item.id !== id
        ))
    }
}

export default new Todo();
