import React, { useState } from "react";
import s from '../../style/todoList/todoList.sass'

function AddList ({ getInput, setinput }) {
    const [statuss, setStatus] = useState(null)


    function deliteTask (id) {
        let newTodo = [...getInput].filter(item => item.id !== id)
        setinput(newTodo)
    }

    function statusTask (id) {
        let newTodo = [...getInput].filter(item => {
            if (item.id === id) {
                item.status = !item.status

                setStatus(item.status)
                console.log(statuss);
            }
            return item
        })
        setinput(newTodo)
    }

    return (
        <div className="task">
            {
                getInput.map((item) => (
                    <div className="task__item" key={item.id}>

                        <p className={statuss == false ? 'task__close' : 's.task__open'} >{item.title}</p>
                        <div className="task__btns">
                            <button onClick={() => deliteTask(item.id)} type="">удалить</button>
                            <button onClick={() => statusTask(item.id)} type="">статус</button>
                        </div>
                    </div>
                ))
            }
        </ div >
    )
}


export default AddList
