import React, { useState } from "react";
import s from '../../style/todoList/todoList.sass'

function AddList ({ getInput, setinput }) {
    const [status, setStatus] = useState(null)
    const [edit, setEdit] = useState(null)


    function deliteTask (id) {
        let newTodo = [...getInput].filter(item => item.id !== id)
        setinput(newTodo)
    }

    function statusTask (id) {
        let newTodo = [...getInput].filter(item => {
            if (item.id === id) {
                item.status = !item.status
                setStatus(item.status)
                setEdit(id)
                console.log(status);
            }
            return item
        })
        setinput(newTodo)
    }

    return (
        <div className="task">
            {
                getInput.map((item) => (
                    <div className="task__item" key={item.id}>
                        {edit == item.id ?
                            <div>
                                <p className={item.status == false ? 'task__close' : 'task__open'} >{item.title}</p>
                            </div> :
                            <p >{item.title}</p>
                        }
                        <div className="task__btns">
                            <button onClick={() => deliteTask(item.id)} type="">удалить</button>
                            {
                                edit == item.id & item.status == false ?
                                    <button onClick={() => statusTask(item.id)} type="">еще не выполнено</button> :
                                    <button onClick={() => statusTask(item.id)} type="">выполнено</button>
                            }

                        </div>
                    </div>
                ))
            }
        </ div >
    )
}


export default AddList
